import React, { useState, useEffect } from 'react';
import { User, Link, Shield, Activity, ArrowUpRight, ArrowDownRight, Search, Target, AlertTriangle } from 'lucide-react';

const NodeDetails = ({ data, pagerankResults, suspiciousNodes }) => {
  const [selectedNode, setSelectedNode] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    if (data?.nodes?.length > 0 && !selectedNode) {
      // انتخاب اولیه: یک نود بذر اگر وجود دارد، در غیر این صورت اولین نود
      const seedNode = data.nodes.find(node => node.group === 2);
      setSelectedNode(seedNode || data.nodes[0]);
    }
  }, [data, selectedNode]);

  if (!data || !data.nodes || data.nodes.length === 0) {
    return (
      <div className="space-y-6 text-center py-10 glass-card">
        <div className="text-cyber-gray-500">No node data available</div>
      </div>
    );
  }

  const filteredNodes = data.nodes.filter(node => 
    node.id.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const getNodeConnections = (nodeId) => {
    if (!data.links) return { outgoing: 0, incoming: 0 };

    const outgoing = data.links.filter(link => link.source === nodeId).length;
    const incoming = data.links.filter(link => link.target === nodeId).length;

    return { outgoing, incoming };
  };

  const getNodeLinks = (nodeId) => {
    return data.links.filter(link => 
      link.source === nodeId || link.target === nodeId
    );
  };

  const getNodeType = (node) => {
    if (node.group === 2) return { label: 'Manual Seed', color: 'text-neon-pink', bg: 'bg-neon-pink/10' };
    if (node.group === 3) return { label: 'Potential Fraud', color: 'text-yellow-500', bg: 'bg-yellow-500/10' };
    return { label: 'Normal Node', color: 'text-neon-cyan', bg: 'bg-neon-cyan/10' };
  };

  return (
    <div className="space-y-6">
      {/* جستجو و انتخاب نود */}
      <div className="glass-card p-4">
        <div className="relative mb-4">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-cyber-gray-400" />
          <input 
            type="text"
            placeholder="Search nodes (e.g. RepAdamSchiff)..."
            className="w-full bg-black/40 border border-white/10 rounded-lg pl-10 pr-4 py-2 text-sm focus:border-neon-cyan outline-none transition-colors text-white"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>

        <div className="flex flex-wrap gap-2 max-h-48 overflow-y-auto pr-2 custom-scrollbar">
          {filteredNodes.slice(0, 30).map(node => {
            const nodeType = getNodeType(node);
            return (
              <button
                key={node.id}
                onClick={() => setSelectedNode(node)}
                className={`px-3 py-1.5 rounded-md text-xs font-mono transition-all ${
                  selectedNode?.id === node.id
                    ? 'bg-neon-cyan text-black font-bold'
                    : `${nodeType.bg} hover:bg-white/10 text-white`
                }`}
              >
                {node.id}
              </button>
            );
          })}
        </div>
      </div>

      {/* نمایش جزئیات نود انتخاب شده */}
      {selectedNode && (
        <div className="glass-card p-6 border-t-2 border-neon-purple/50">
          <div className="flex flex-col md:flex-row md:items-center justify-between gap-4 mb-6">
            <div className="flex items-center space-x-4">
              <div className={`p-4 rounded-xl ${getNodeType(selectedNode).bg}`}>
                {selectedNode.group === 2 ? (
                  <Shield className="w-8 h-8 text-neon-pink" />
                ) : selectedNode.group === 3 ? (
                  <AlertTriangle className="w-8 h-8 text-yellow-500" />
                ) : (
                  <User className="w-8 h-8 text-neon-cyan" />
                )}
              </div>
              <div>
                <h3 className="text-2xl font-bold font-mono text-white">@{selectedNode.id}</h3>
                <div className="flex items-center gap-2 mt-1">
                  <span 
                    className={`w-2 h-2 rounded-full ${getNodeType(selectedNode).color}`}
                  ></span>
                  <p className={`text-sm uppercase tracking-widest ${getNodeType(selectedNode).color}`}>
                    {getNodeType(selectedNode).label}
                  </p>
                </div>
              </div>
            </div>

            <div className="bg-white/5 p-4 rounded-xl text-center md:text-right border border-white/5">
              <p className="text-3xl font-black text-neon-purple font-mono">
                {((selectedNode.ppr || 0) * 100).toFixed(4)}%
              </p>
              <p className="text-[10px] uppercase tracking-tighter text-cyber-gray-500 font-bold">PPR Score</p>
              {suspiciousNodes?.[selectedNode.id] && (
                <p className="text-xs text-neon-pink mt-1">
                  Seed Confidence: {(suspiciousNodes[selectedNode.id] * 100).toFixed(0)}%
                </p>
              )}
            </div>
          </div>

          {/* متریک‌های چهارگانه */}
          <div className="grid grid-cols-2 lg:grid-cols-4 gap-4 mb-8">
            <div className="bg-black/20 p-4 rounded-lg border border-white/5 text-center">
              <span className="text-[10px] text-cyber-gray-400 uppercase block mb-1">Outgoing Links</span>
              <p className="text-xl font-bold text-neon-cyan">
                {getNodeConnections(selectedNode.id).outgoing}
              </p>
            </div>
            <div className="bg-black/20 p-4 rounded-lg border border-white/5 text-center">
              <span className="text-[10px] text-cyber-gray-400 uppercase block mb-1">Incoming Links</span>
              <p className="text-xl font-bold text-neon-pink">
                {getNodeConnections(selectedNode.id).incoming}
              </p>
            </div>
            <div className="bg-black/20 p-4 rounded-lg border border-white/5 text-center">
              <span className="text-[10px] text-cyber-gray-400 uppercase block mb-1">Transactions</span>
              <p className="text-xl font-bold">{selectedNode.transactions || 'N/A'}</p>
            </div>
            <div className="bg-black/20 p-4 rounded-lg border border-white/5 text-center">
              <span className="text-[10px] text-cyber-gray-400 uppercase block mb-1">Fraud Risk</span>
              <p className={`text-xl font-bold ${
                selectedNode.group === 2 ? 'text-neon-pink' : 
                selectedNode.group === 3 ? 'text-yellow-500' : 'text-green-400'
              }`}>
                {selectedNode.group === 2 ? 'HIGH' : 
                 selectedNode.group === 3 ? 'MEDIUM' : 'LOW'}
              </p>
            </div>
          </div>

          {/* لیست اتصالات */}
          <div>
            <h4 className="text-sm font-bold uppercase mb-4 text-cyber-gray-400 flex items-center">
              <Link className="w-4 h-4 mr-2" />
              Network Connections ({getNodeLinks(selectedNode.id).length})
            </h4>
            <div className="space-y-2 max-h-60 overflow-y-auto pr-2 custom-scrollbar">
              {getNodeLinks(selectedNode.id).map((link, index) => {
                const isOutgoing = link.source === selectedNode.id;
                const connectedWith = isOutgoing ? link.target : link.source;

                return (
                  <div key={`link-${index}`} className="flex items-center justify-between p-3 bg-white/5 rounded-lg border border-white/5 hover:bg-white/10 transition-colors">
                    <div className="flex items-center gap-3">
                      <div className={`p-1.5 rounded ${isOutgoing ? 'text-neon-cyan bg-neon-cyan/10' : 'text-neon-pink bg-neon-pink/10'}`}>
                        {isOutgoing ? <ArrowUpRight size={14}/> : <ArrowDownRight size={14}/>}
                      </div>
                      <span className="font-mono text-sm text-white">@{connectedWith}</span>
                    </div>
                    <div className="text-right">
                      <span className="text-[10px] text-cyber-gray-500 font-mono">
                        Weight: {(link.value || 1).toFixed(2)}
                      </span>
                      <div className="text-[9px] text-cyber-gray-600 mt-1">
                        {isOutgoing ? 'Outgoing' : 'Incoming'}
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default NodeDetails;
