import React from 'react';
import { AlertTriangle, TrendingUp, DollarSign, Activity, ShieldAlert, UserSearch } from 'lucide-react';

const FraudMetrics = ({ data, pagerankResults }) => {
  if (!data || !data.nodes) {
    return (
      <div className="space-y-4">
        <div className="glass-card p-6 text-center">
          <div className="text-cyber-gray-500">No data available</div>
        </div>
      </div>
    );
  }

  // جدا کردن نودهای بذر (Seeds) از بقیه نودها
  const initialSeeds = data.nodes.filter(node => node.suspicious);

  // پیدا کردن نودهایی که ما معرفی نکردیم ولی امتیاز PPR بالایی گرفتند (Top Candidates)
  const newlyDetectedIds = (pagerankResults?.top_fraud_candidates || [])
    .filter(id => !initialSeeds.find(s => s.id === id))
    .slice(0, 5); // فقط ۵ مورد برتر جدید

  const totalTransactionValue = data.links.reduce((sum, link) => sum + (link.value || 0), 0);

  return (
    <div className="space-y-4 max-h-[80vh] overflow-y-auto pr-2 custom-scrollbar">

      {/* بخش اول: نودهای مشکوک اولیه (Manual Seeds) */}
      <div className="space-y-2">
        <div className="flex items-center space-x-2 px-1 mb-2">
          <ShieldAlert className="w-4 h-4 text-neon-pink" />
          <h3 className="text-sm font-bold uppercase tracking-wider text-neon-pink">Initial Seed Set</h3>
        </div>
        {initialSeeds.slice(0, 5).map((node) => {
          // برای بذرها، اولویت نمایش با همان امتیاز تعریفی (Suspicion Score) است
          const displayScore = (node.suspicionScore || 0) * 100;
          return (
            <div key={node.id} className="glass-card p-3 border-l-4 border-neon-pink bg-neon-pink/5">
              <div className="flex justify-between items-center">
                <div>
                  <p className="font-mono text-sm">@{node.id}</p>
                  <p className="text-[10px] text-cyber-gray-400">Target Source</p>
                </div>
                <div className="text-right">
                  <p className="text-neon-pink font-bold">{displayScore.toFixed(0)}%</p>
                  <p className="text-[10px] uppercase">Suspicion</p>
                </div>
              </div>
            </div>
          );
        })}
      </div>

      {/* بخش دوم: نودهای شناسایی شده توسط الگوریتم (PPR Discovery) */}
      <div className="space-y-2 pt-2">
        <div className="flex items-center space-x-2 px-1 mb-2">
          <UserSearch className="w-4 h-4 text-neon-cyan" />
          <h3 className="text-sm font-bold uppercase tracking-wider text-neon-cyan">Newly Detected (Guilt by Association)</h3>
        </div>
        {newlyDetectedIds.length > 0 ? (
          newlyDetectedIds.map((id) => {
            const node = data.nodes.find(n => n.id === id);
            // نمایش قدرت ارتباط بر اساس PPR
            const pprImpact = (node?.ppr || 0) * 1000; 
            return (
              <div key={id} className="glass-card p-3 border-l-4 border-neon-cyan">
                <div className="flex justify-between items-center">
                  <div>
                    <p className="font-mono text-sm">@{id}</p>
                    <p className="text-[10px] text-cyber-gray-400">Linked via Network</p>
                  </div>
                  <div className="text-right">
                    <p className="text-neon-cyan font-bold">{pprImpact.toFixed(2)}%</p>
                    <p className="text-[10px] uppercase">Risk Factor</p>
                  </div>
                </div>
              </div>
            );
          })
        ) : (
          <p className="text-xs text-center text-cyber-gray-500 py-2 italic">Run analysis to discover links</p>
        )}
      </div>

      {/* خلاصه وضعیت سیستم */}
      <div className="grid grid-cols-2 gap-3 pt-4 border-t border-white/10">
        <div className="glass-card p-3 text-center">
          <TrendingUp className="w-4 h-4 text-neon-cyan mx-auto mb-1" />
          <p className="text-lg font-bold leading-none">{initialSeeds.length}</p>
          <p className="text-[10px] text-cyber-gray-400 uppercase">Seeds</p>
        </div>
        <div className="glass-card p-3 text-center">
          <DollarSign className="w-4 h-4 text-neon-purple mx-auto mb-1" />
          <p className="text-lg font-bold leading-none">${(totalTransactionValue / 1000).toFixed(1)}K</p>
          <p className="text-[10px] text-cyber-gray-400 uppercase">Volume</p>
        </div>
      </div>

      {/* وضعیت الگوریتم (خواسته استاد: Convergence) */}
      {pagerankResults?.convergence_info && (
        <div className="p-3 glass-card bg-white/5">
          <div className="flex items-center justify-between mb-2">
            <div className="flex items-center space-x-2">
              <Activity className="w-3 h-3 text-neon-purple" />
              <span className="text-[10px] font-bold uppercase">Power Iteration Info</span>
            </div>
            <span className={`text-[10px] px-2 py-0.5 rounded-full ${
              pagerankResults.convergence_info.converged ? 'bg-green-500/20 text-green-400' : 'bg-yellow-500/20 text-yellow-400'
            }`}>
              {pagerankResults.convergence_info.converged ? 'CONVERGED' : 'STABILIZING'}
            </span>
          </div>
          <div className="grid grid-cols-2 gap-2 text-[10px]">
            <div className="flex justify-between">
              <span className="text-cyber-gray-400">Iterations:</span>
              <span className="text-white font-mono">{pagerankResults.convergence_info.iterations_performed}</span>
            </div>
            <div className="flex justify-between">
              <span className="text-cyber-gray-400">Damping (α):</span>
              <span className="text-white font-mono">0.15</span>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default FraudMetrics;
