import React, { useState, useEffect } from 'react';
import { 
  AlertTriangle, Activity, Cpu, Shield, Network, Zap, Users, 
  BarChart3, Play, RefreshCw, AlertCircle
} from 'lucide-react';
import GraphVisualization from './components/GraphVisualization';
import FraudMetrics from './components/FraudMetrics';
import NodeDetails from './components/NodeDetails';
import SystemMonitor from './components/SystemMonitor';
import api from './services/api';

function App() {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [graphData, setGraphData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [apiConnected, setApiConnected] = useState(false);
  const [pagerankResults, setPagerankResults] = useState(null);
  const [systemStats, setSystemStats] = useState({
    nodes: 0,
    edges: 0,
    suspiciousNodes: 0,
    pprTime: '0.0ms'
  });
  const [error, setError] = useState(null);
  const [analysisRunning, setAnalysisRunning] = useState(false);

  useEffect(() => {
    checkApiConnection();
    loadInitialData();
  }, []);

  const checkApiConnection = async () => {
    try {
      const health = await api.healthCheck();
      setApiConnected(health.status === 'healthy');
    } catch (err) {
      setApiConnected(false);
    }
  };

  const loadInitialData = async () => {
    setLoading(true);
    try {
      // صدا زدن متد جدید برای دیتای کنگره
      const response = await api.loadCongressGraph(); 
      if (response.success) {
        const graphResponse = await api.getCurrentGraph();

        // ساخت گراف اولیه بدون امتیاز PPR
        const formattedData = {
          nodes: graphResponse.nodes.map(id => ({ id, group: 1, ppr: 0 })),
          links: graphResponse.edges.map(e => ({ source: e.source, target: e.target, value: e.weight }))
        };

        setGraphData(formattedData);
        setSystemStats(prev => ({
          ...prev,
          nodes: graphResponse.node_count,
          edges: graphResponse.edges.length
        }));
        setApiConnected(true);
      }
    } catch (err) {
      setError("خطا در بارگذاری دیتای کنگره");
    } finally {
      setLoading(false);
    }
  };

  // تابع تبدیل دیتا که ارور می‌داد (حالا کاملاً اصلاح شده)
  const transformDataForFrontend = (graphResponse, pprResponse, suspiciousNodes) => {
    if (!graphResponse || !pprResponse) return { graphData: null, systemStats: {} };

    const nodes = graphResponse.nodes.map(nodeId => {
      const pprScore = pprResponse.pagerank_scores?.[nodeId] || 0;
      const suspicion = suspiciousNodes[nodeId] || 0;

      let group = 1;
      if (pprScore > 0.005) group = 2;
      if (pprScore > 0.01) group = 3;
      if (suspicion > 0.5) group = 4;

      return {
        id: nodeId,
        group: group,
        ppr: pprScore,
        suspicious: suspicion > 0.5,
        transactions: Math.floor(pprScore * 100000),
        suspicionScore: suspicion
      };
    });

    const links = graphResponse.edges.map(e => ({
      source: e.source,
      target: e.target,
      value: e.weight
    }));

    return {
      graphData: { nodes, links },
      systemStats: {
        nodes: graphResponse.node_count || nodes.length,
        edges: links.length,
        suspiciousNodes: Object.keys(suspiciousNodes).length,
        pprTime: `${pprResponse.compute_time_ms?.toFixed(1) || '0.0'}ms`
      }
    };
  };

  const computePageRank = async (suspiciousNodes) => {
    setAnalysisRunning(true);
    try {
      const pprResponse = await api.computePageRank({
        damping_factor: 0.85,
        max_iterations: 100,
        tolerance: 1e-8,
        suspicious_nodes: suspiciousNodes
      });

      if (pprResponse.success) {
        setPagerankResults(pprResponse);
        const graphResponse = await api.getCurrentGraph();

        const transformed = transformDataForFrontend(
          graphResponse,
          pprResponse,
          suspiciousNodes
        );

        setGraphData(transformed.graphData);
        setSystemStats(transformed.systemStats);
      }
    } catch (err) {
      setError(err.message);
    } finally {
      setAnalysisRunning(false);
    }
  };

  const handleStartAnalysis = async () => {
    if (apiConnected) {
      // نودهای رندوم برای تست روی دیتای واقعی
      await computePageRank({
        'RepAdamSchiff': 0.8,
        'SpeakerPelosi': 0.9,
        'SenSchumer': 0.7
      });
    }
  };

  const handleRefresh = () => loadInitialData();

  const tabs = [
    { id: 'dashboard', label: 'Dashboard', icon: Activity },
    { id: 'graph', label: 'Network Graph', icon: Network },
    { id: 'analytics', label: 'Analytics', icon: BarChart3 },
    { id: 'monitor', label: 'System Monitor', icon: Cpu },
  ];

  return (
    <div className="min-h-screen bg-deep-black bg-cyber-grid text-white">
      {/* Header */}
      <header className="border-b border-neon-cyan/20 p-4">
        <div className="container mx-auto flex justify-between items-center">
          <div className="flex items-center space-x-3">
            <Shield className="text-neon-cyan w-8 h-8" />
            <h1 className="text-2xl font-bold text-neon-cyan">Fraud Detection - Congress Edition</h1>
          </div>
          <button 
            onClick={handleStartAnalysis}
            disabled={analysisRunning || !apiConnected}
            className="bg-gradient-to-r from-neon-cyan to-neon-purple px-6 py-2 rounded-lg font-bold disabled:opacity-50"
          >
            {analysisRunning ? "Analyzing..." : "Run PageRank Analysis"}
          </button>
        </div>
      </header>

      <main className="container mx-auto p-6">
        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
          <div className="glass-card p-4">
            <p className="text-cyber-gray-400 text-sm">Nodes</p>
            <p className="text-2xl font-bold text-neon-cyan">{systemStats.nodes}</p>
          </div>
          <div className="glass-card p-4">
            <p className="text-cyber-gray-400 text-sm">Edges</p>
            <p className="text-2xl font-bold text-white">{systemStats.edges}</p>
          </div>
          <div className="glass-card p-4">
            <p className="text-cyber-gray-400 text-sm">Suspicious</p>
            <p className="text-2xl font-bold text-neon-pink">{systemStats.suspiciousNodes}</p>
          </div>
          <div className="glass-card p-4">
            <p className="text-cyber-gray-400 text-sm">PPR Time</p>
            <p className="text-2xl font-bold text-neon-purple">{systemStats.pprTime}</p>
          </div>
        </div>

        {/* Graph Area */}
        <div className="glass-card p-6 h-[500px]">
           {loading ? (
             <div className="flex items-center justify-center h-full">Loading Congress Network...</div>
           ) : (
             <GraphVisualization data={graphData} interactive={true} />
           )}
        </div>
      </main>
    </div>
  );
}

export default App;
