import React from 'react';
import { AlertTriangle, TrendingUp, DollarSign, Activity, ShieldAlert, UserSearch } from 'lucide-react';

const FraudMetrics = ({ data, pagerankResults }) => {
  if (!data || !data.nodes) {
    return (
      <div className="space-y-4">
        <div className="glass-card p-6 text-center">
          <div className="text-cyber-gray-500">No data available</div>
        </div>
      </div>
    );
  }

  // تابع کمکی برای استخراج امن ID (مانع از خطای Objects are not valid as a React child)
  const getSafeId = (item) => {
    if (typeof item === 'object' && item !== null) {
      return item.node_id || item.id || "Unknown";
    }
    return item || "Unknown";
  };

  // جدا کردن نودهای بذر (Seeds)
  const initialSeeds = data.nodes.filter(node => node.suspicious);

  // پیدا کردن نودهایی که توسط الگوریتم شناسایی شده‌اند
  const newlyDetectedItems = (pagerankResults?.top_fraud_candidates || [])
    .filter(item => {
      const id = getSafeId(item);
      return !initialSeeds.find(s => s.id === id);
    })
    .slice(0, 5);

  const totalTransactionValue = data.links.reduce((sum, link) => sum + (link.value || 0), 0);

  return (
    <div className="space-y-4 max-h-[80vh] overflow-y-auto pr-2 custom-scrollbar">

      {/* بخش اول: نودهای مشکوک اولیه */}
      <div className="space-y-2">
        <div className="flex items-center space-x-2 px-1 mb-2">
          <ShieldAlert className="w-4 h-4 text-neon-pink" />
          <h3 className="text-sm font-bold uppercase tracking-wider text-neon-pink">Initial Seed Set</h3>
        </div>
        {initialSeeds.length > 0 ? initialSeeds.slice(0, 5).map((node) => (
          <div key={node.id} className="glass-card p-3 border-l-4 border-neon-pink bg-neon-pink/5">
            <div className="flex justify-between items-center">
              <div>
                <p className="font-mono text-sm text-white">@{node.id}</p>
                <p className="text-[10px] text-cyber-gray-400">Target Source</p>
              </div>
              <div className="text-right">
                <p className="text-neon-pink font-bold">{((node.suspicionScore || 0) * 100).toFixed(0)}%</p>
                <p className="text-[10px] uppercase text-cyber-gray-500">Suspicion</p>
              </div>
            </div>
          </div>
        )) : (
          <p className="text-[10px] text-center text-cyber-gray-500">No seeds defined</p>
        )}
      </div>

      {/* بخش دوم: نودهای شناسایی شده توسط الگوریتم (اصلاح شده برای جلوگیری از خطا) */}
      <div className="space-y-2 pt-2">
        <div className="flex items-center space-x-2 px-1 mb-2">
          <UserSearch className="w-4 h-4 text-neon-cyan" />
          <h3 className="text-sm font-bold uppercase tracking-wider text-neon-cyan">Newly Detected (AI Scan)</h3>
        </div>
        {newlyDetectedItems.length > 0 ? (
          newlyDetectedItems.map((item, idx) => {
            const id = getSafeId(item);
            const risk = typeof item === 'object' ? item.risk_score : 0;

            return (
              <div key={`new-${id}-${idx}`} className="glass-card p-3 border-l-4 border-neon-cyan">
                <div className="flex justify-between items-center">
                  <div>
                    <p className="font-mono text-sm text-white">@{id}</p>
                    <p className="text-[10px] text-cyber-gray-400">Guilt by Association</p>
                  </div>
                  <div className="text-right">
                    <p className="text-neon-cyan font-bold">{(risk * 100).toFixed(2)}%</p>
                    <p className="text-[10px] uppercase text-cyber-gray-500">PPR Risk</p>
                  </div>
                </div>
              </div>
            );
          })
        ) : (
          <div className="text-center py-4 glass-card border-dashed border-white/10">
            <p className="text-[10px] text-cyber-gray-500 italic">Analyzing network patterns...</p>
          </div>
        )}
      </div>

      {/* خلاصه وضعیت */}
      <div className="grid grid-cols-2 gap-3 pt-2">
        <div className="glass-card p-3 text-center border-b-2 border-neon-cyan/30">
          <TrendingUp className="w-4 h-4 text-neon-cyan mx-auto mb-1" />
          <p className="text-lg font-bold text-white">{initialSeeds.length}</p>
          <p className="text-[9px] text-cyber-gray-400 uppercase">Input Seeds</p>
        </div>
        <div className="glass-card p-3 text-center border-b-2 border-neon-purple/30">
          <DollarSign className="w-4 h-4 text-neon-purple mx-auto mb-1" />
          <p className="text-lg font-bold text-white">${(totalTransactionValue / 1000).toFixed(1)}K</p>
          <p className="text-[9px] text-cyber-gray-400 uppercase">Net Volume</p>
        </div>
      </div>

      {/* اطلاعات فنی الگوریتم برای استاد */}
      {pagerankResults?.convergence_info && (
        <div className="p-3 glass-card bg-white/5 border border-white/10">
          <div className="flex items-center justify-between mb-2">
            <div className="flex items-center space-x-2">
              <Activity className="w-3 h-3 text-neon-purple" />
              <span className="text-[10px] font-bold uppercase text-neon-purple">Power Iteration</span>
            </div>
            <span className={`text-[9px] px-2 py-0.5 rounded-sm font-bold ${
              pagerankResults.convergence_info.converged ? 'bg-green-500/20 text-green-400' : 'bg-yellow-500/20 text-yellow-400'
            }`}>
              {pagerankResults.convergence_info.converged ? 'CONVERGED' : 'RUNNING'}
            </span>
          </div>
          <div className="flex justify-between text-[10px] font-mono">
            <span className="text-cyber-gray-500">ITERATIONS:</span>
            <span className="text-white">{pagerankResults.convergence_info.iterations_performed}</span>
          </div>
        </div>
      )}
    </div>
  );
};

export default FraudMetrics;
