import React, { useRef, useEffect, useState } from 'react';
import ForceGraph2D from 'react-force-graph-2d';

const GraphVisualization = ({ data, interactive = false }) => {
  const graphRef = useRef();
  const [dimensions, setDimensions] = useState({ width: 800, height: 400 });

  useEffect(() => {
    const updateDimensions = () => {
      const container = graphRef.current?.parentElement;
      if (container) {
        setDimensions({
          width: container.clientWidth,
          height: container.clientHeight
        });
      }
    };
    updateDimensions();
    window.addEventListener('resize', updateDimensions);
    return () => window.removeEventListener('resize', updateDimensions);
  }, []);

  useEffect(() => {
    if (graphRef.current && data) {
      graphRef.current.d3Force('charge').strength(-300);
      graphRef.current.d3Force('link').distance(100);
    }
  }, [data]);

  if (!data || !data.nodes || !data.links) {
    return (
      <div className="h-full w-full flex items-center justify-center">
        <div className="text-center">
          <div className="text-cyber-gray-500 text-lg mb-2">No Graph Data</div>
        </div>
      </div>
    );
  }

  return (
    <div className="relative h-full w-full">
      <div className="absolute inset-0 bg-gradient-to-br from-neon-cyan/5 to-neon-purple/5 rounded-lg"></div>
      <ForceGraph2D
        ref={graphRef}
        graphData={data}
        nodeLabel="id"
        // تنظیم سایز: صورتی‌ها سایز ۸، طلایی‌ها سایز ۱۰ (کمی بزرگتر) و بقیه سایز ۵
        nodeRelSize={1}
        nodeCanvasObject={(node, ctx, globalScale) => {
          const label = node.id;
          const fontSize = 12/globalScale;
          ctx.font = `${fontSize}px Sans-Serif`;

          // تعیین رنگ و شعاع دایره
          let radius = 5;
          let color = '#22d3ee'; // آبی معمولی

          if (node.suspicious) {
            color = '#ec4899'; // صورتی (بذرها)
            radius = 7; 
          } else if (node.ppr > 0.1) {
            color = '#FFD700'; // طلایی پررنگ (شناسایی شده‌ها)
            radius = 9; // کمی بزرگتر از صورتی طبق خواسته شما
          }

          // رسم دایره نود
          ctx.beginPath();
          ctx.arc(node.x, node.y, radius, 0, 2 * Math.PI, false);
          ctx.fillStyle = color;
          ctx.fill();

          // اضافه کردن درخشش (Glow) برای نودهای طلایی و صورتی
          if (node.suspicious || node.ppr > 0.1) {
            ctx.shadowBlur = 15;
            ctx.shadowColor = color;
          }

          // نمایش متن زیر نود در زوم بالا
          if (globalScale > 1.5) {
            ctx.textAlign = 'center';
            ctx.textBaseline = 'middle';
            ctx.fillStyle = 'rgba(255, 255, 255, 0.8)';
            ctx.fillText(label, node.x, node.y + radius + 5);
          }
        }}
        linkColor={() => 'rgba(34, 211, 238, 0.2)'}
        linkWidth={link => Math.sqrt(link.value || 1) / 50}
        linkDirectionalParticles={2}
        linkDirectionalParticleWidth={2}
        backgroundColor="rgba(2, 6, 23, 0)"
        width={dimensions.width}
        height={dimensions.height}
        enableNodeDrag={interactive}
        enableZoomInteraction={interactive}
        enablePanInteraction={interactive}
      />

      {/* راهنمای رنگ‌ها (Legend) در گوشه گراف */}
      <div className="absolute bottom-4 left-4 bg-black/60 p-2 rounded border border-white/10 text-[10px] space-y-1">
        <div className="flex items-center gap-2">
          <div className="w-3 h-3 rounded-full bg-[#ec4899]"></div>
          <span className="text-white">Manual Seed</span>
        </div>
        <div className="flex items-center gap-2">
          <div className="w-3 h-3 rounded-full bg-[#FFD700]"></div>
          <span className="text-white">AI Detected (>0.1)</span>
        </div>
        <div className="flex items-center gap-2">
          <div className="w-3 h-3 rounded-full bg-[#22d3ee]"></div>
          <span className="text-white">Normal Node</span>
        </div>
      </div>
    </div>
  );
};

export default GraphVisualization;
